let modalClass="js-modal",modalContentClass="js-modal-content";const modal=document.querySelectorAll(`.${modalClass}`);if(modal){const a=document.querySelectorAll("[data-modal]");a.forEach(e=>{e.addEventListener("click",o=>{openModal("#"+e.dataset.modal)})});const b=document.querySelectorAll("js-modal-close");b.forEach(o=>{o.addEventListener("click",o=>{closeModal()})});const c=document.querySelectorAll(`.${modalContentClass}`);c.forEach(o=>{o.addEventListener("click",o=>{o.stopPropagation()})}),modal.forEach(o=>{o.addEventListener("click",o=>{closeModal()})})}function openModal(o){document.querySelector(o).classList.add("active"),document.body.classList.add("lock")}function closeModal(){const o=document.querySelector(`${modalClass}.active`);o&&(o.classList.remove("active"),document.body.classList.remove("lock"))}